import React, { useEffect, useState } from 'react';
import { apiGetOrdersByStoreId, apiUpdateOrderStatus } from '../api';
import 'bootstrap/dist/css/bootstrap.min.css';

function OrderListPageSeller() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');
  const userId = sessionStorage.getItem('userId');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const storeResponse = await fetch(`http://localhost:5197/api/Store/by-user-id/${userId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!storeResponse.ok) {
          throw new Error('Failed to fetch store');
        }

        const storeData = await storeResponse.json();
        const storeId = storeData.storeId;

        const ordersResponse = await apiGetOrdersByStoreId(storeId, token);
        setOrders(ordersResponse);
      } catch (error) {
        setError('An error occurred. Please try again.');
      }
    };

    fetchOrders();
  }, [token, userId]);

  const handleUpdateStatus = async (orderId, status) => {
    try {
      await apiUpdateOrderStatus(orderId, status, token);
      const updatedOrders = orders.map((order) =>
        order.orderId === orderId ? { ...order, status } : order
      );
      setOrders(updatedOrders);
    } catch (error) {
      setError('Failed to update order status');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Orders</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      {orders.length > 0 ? (
        <div className="table-responsive">
          <table className="table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Order Date</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order) => (
                <tr key={order.orderId}>
                  <td>{order.orderId}</td>
                  <td>{new Date(order.orderDate).toLocaleString()}</td>
                  <td>{order.status}</td>
                  <td>
                    <button
                      className="btn btn-primary mr-2"
                      onClick={() => handleUpdateStatus(order.orderId, 'Completed')}
                    >
                      Mark as Completed
                    </button>
                    <button
                      className="btn btn-secondary"
                      onClick={() => handleUpdateStatus(order.orderId, 'Not Completed')}
                    >
                      Mark as Not Completed
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No orders found for this store.</p>
      )}
    </div>
  );
}

export default OrderListPageSeller;
