import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { apiGetOrdersByStoreId, apiUpdateOrderStatus } from '../api';

function OrderPageSeller() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const userId = sessionStorage.getItem('userId');
  const token = localStorage.getItem('authToken');

  const fetchOrders = async () => {
    try {
      const response = await apiGetOrdersByStoreId(userId, token);
      setOrders(response);
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleStatusChange = async (orderId, status) => {
    try {
      await apiUpdateOrderStatus(orderId, status, token);
      fetchOrders();
    } catch (error) {
      setError('Failed to update order status');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Orders</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {orders.map(order => (
          <div key={order.orderId} className="col-md-4 mb-4">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Order ID: {order.orderId}</h5>
                <p className="card-text">User ID: {order.userId}</p>
                <p className="card-text">Order Date: {order.orderDate}</p>
                <p className="card-text">Status: {order.status}</p>
                <div className="form-group">
                  <label htmlFor={`status-${order.orderId}`}>Change Status</label>
                  <select
                    id={`status-${order.orderId}`}
                    className="form-control"
                    value={order.status}
                    onChange={(e) => handleStatusChange(order.orderId, e.target.value)}
                  >
                    <option value="Pending">Pending</option>
                    <option value="Completed">Completed</option>
                    <option value="Not Completed">Not Completed</option>
                  </select>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default OrderPageSeller;
