import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { apiGetOrderById, apiUpdateOrderStatus } from '../api';

function EditOrderPage() {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const token = localStorage.getItem('authToken');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const orderData = await apiGetOrderById(orderId, token);
        setOrder(orderData);
        setStatus(orderData.status);
      } catch (error) {
        setError('Failed to fetch order');
      }
    };

    fetchOrder();
  }, [orderId, token]);

  const handleUpdateStatus = async (e) => {
    e.preventDefault();
    try {
      await apiUpdateOrderStatus(orderId, status, token);
      setSuccess(true);
      setError(null);
      navigate('/orders'); // Redirect to orders page after successful update
    } catch (error) {
      setSuccess(false);
      setError('Failed to update order status');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Edit Order Status</h1>
      {order && (
        <form onSubmit={handleUpdateStatus}>
          <div className="mb-3">
            <label htmlFor="status" className="form-label">Status</label>
            <select
              className="form-control"
              id="status"
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              required
            >
              <option value="Pending">Pending</option>
              <option value="Completed">Completed</option>
              <option value="Not Completed">Not Completed</option>
            </select>
          </div>
          <button type="submit" className="btn btn-success">Update Status</button>
        </form>
      )}
      {success && <div className="alert alert-success mt-4">Order status updated successfully!</div>}
      {error && <div className="alert alert-danger mt-4">{error}</div>}
    </div>
  );
}

export default EditOrderPage;
