// OrderPageBuyer.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { apiCreateOrder } from '../api';

function OrderPageBuyer() {
  const { storeId } = useParams();
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const token = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://localhost:5197/api/Product/by-store-id/${storeId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          setError('Failed to fetch products');
        }
      } catch (error) {
        setError('An error occurred. Please try again.');
      }
    };

    if (storeId) {
      fetchProducts();
    }
  }, [storeId, token]);

  const handleAddProduct = (productId, quantity) => {
    setSelectedProducts([...selectedProducts, { productId, quantity }]);
  };

  const handleOrder = async () => {
    const orderData = {
      storeId,
      orderItems: selectedProducts,
    };

    try {
      await apiCreateOrder(orderData, token);
      setSuccess('Order placed successfully!');
      setError(null);
      setSelectedProducts([]);
    } catch (error) {
      setSuccess(null);
      setError('Failed to place order');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Order Products</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}
      <div className="row">
        {products.map((product) => (
          <div className="col-md-4 mb-4" key={product.productId}>
            <div className="card">
              <img src={`http://localhost:5197${product.imageUrl}`} className="card-img-top" alt={product.productName} />
              <div className="card-body">
                <h5 className="card-title">{product.productName}</h5>
                <p className="card-text">{product.description}</p>
                <p className="card-text"><strong>Price:</strong> ${product.price}</p>
                <p className="card-text"><strong>Stock:</strong> {product.stockQuantity}</p>
                <button onClick={() => handleAddProduct(product.productId, 1)} className="btn btn-primary">Add to Order</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <button onClick={handleOrder} className="btn btn-success mt-4">Place Order</button>
    </div>
  );
}

export default OrderPageBuyer;
