import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function OrderPageBuyer() {
  const { storeId } = useParams();
  const [orderItems, setOrderItems] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');

  const fetchProducts = async () => {
    try {
      const response = await fetch(`http://localhost:5197/api/Product/by-store-id/${storeId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setOrderItems(data.map(product => ({ productId: product.productId, quantity: 1 })));
      } else {
        setError('Failed to fetch products');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [storeId, token]);

  const handleQuantityChange = (productId, quantity) => {
    setOrderItems(orderItems.map(item => item.productId === productId ? { ...item, quantity } : item));
  };

  const handleSubmitOrder = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5197/api/order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          storeId,
          orderItems: orderItems.map(item => ({ productId: item.productId, quantity: item.quantity }))
        }),
      });

      if (response.ok) {
        alert('Order placed successfully');
        setError(null);
      } else {
        setError('Failed to place order');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Place Order</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <form onSubmit={handleSubmitOrder}>
        {orderItems.map(item => (
          <div key={item.productId} className="mb-3">
            <label>Product ID: {item.productId}</label>
            <input
              type="number"
              className="form-control"
              value={item.quantity}
              onChange={(e) => handleQuantityChange(item.productId, parseInt(e.target.value))}
              min="1"
              required
            />
          </div>
        ))}
        <button type="submit" className="btn btn-primary">Place Order</button>
      </form>
    </div>
  );
}

export default OrderPageBuyer;
