import React, { useState, useEffect } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

function StorePage() {
  const [storeName, setStoreName] = useState("");
  const [stores, setStores] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');

  useEffect(() => {
    // Fetch the user's stores when the component mounts
    fetch('http://localhost:5197/api/store', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then(response => response.json())
      .then(data => setStores(data))
      .catch(err => console.error(err));
  }, [token]);

  const handleCreateStore = async () => {
    try {
      const formData = new FormData();
      formData.append('storeName', storeName);

      const response = await fetch('http://localhost:5197/api/store', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData,
      });

      if (response.ok) {
        const newStore = await response.json();
        setStores([...stores, newStore]);
        setStoreName("");
        setError(null);
      } else {
        const data = await response.json();
        setError(data.message || "Failed to create store");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div>
      <TextField
        variant="outlined"
        value={storeName}
        onChange={(e) => setStoreName(e.target.value)}
        label="Store Name"
        fullWidth
        margin="normal"
      />
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <Button onClick={handleCreateStore} variant="contained" color="primary">
        CREATE STORE
      </Button>
      <h2>Your Stores</h2>
      <ul>
        {stores.map(store => (
          <li key={store.storeId}>{store.storeName}</li>
        ))}
      </ul>
    </div>
  );
}

export default StorePage;
