import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import ProductList from './ProductList';

function SearchPage() {
  const [query, setQuery] = useState('');
  const [stores, setStores] = useState([]);
  const [selectedStoreId, setSelectedStoreId] = useState(null);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5197/api/Store/search/${query}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setStores(data);
        setError(null);
      } else {
        setError('Failed to search for stores');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  const handleStoreSelect = (storeId) => {
    setSelectedStoreId(storeId);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Search for Stores</h1>
      <form onSubmit={handleSearch}>
        <div className="mb-3">
          <label htmlFor="query" className="form-label">Store Name</label>
          <input
            type="text"
            className="form-control"
            id="query"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Enter store name"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Search</button>
      </form>
      {error && <div className="alert alert-danger mt-4">{error}</div>}
      <div className="mt-5">
        <h2>Stores</h2>
        <ul className="list-group">
          {stores.map(store => (
            <li
              key={store.storeId}
              className="list-group-item"
              onClick={() => handleStoreSelect(store.storeId)}
              style={{ cursor: 'pointer' }}
            >
              {store.storeName}
            </li>
          ))}
        </ul>
      </div>
      {selectedStoreId && (
        <div className="mt-5">
          <h2>Products</h2>
          <ProductList storeId={selectedStoreId} />
        </div>
      )}
    </div>
  );
}

export default SearchPage;
