import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { apiSearchStores } from '../api';

function SearchPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const data = await apiSearchStores(searchTerm);
      setResults(data);
      setError(null);
    } catch (err) {
      setError('Failed to search stores');
    }
  };

  const handleStoreClick = (storeId) => {
    navigate(`/store/${storeId}`);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Search Stores</h1>
      <form onSubmit={handleSearch}>
        <div className="mb-3">
          <input
            type="text"
            className="form-control"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search for stores"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Search</button>
      </form>
      {error && <div className="alert alert-danger mt-4">{error}</div>}
      <div className="row mt-4">
        {results.map((store) => (
          <div key={store.storeId} className="col-md-4 mb-4">
            <div className="card" onClick={() => handleStoreClick(store.storeId)}>
              <div className="card-body">
                <h5 className="card-title">{store.storeName}</h5>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchPage;
