import React, { useState, useEffect } from 'react';
import { apiGetOrdersByStore, apiUpdateOrderStatus } from '../api';

function OrderListPageSeller() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');
  const storeName = sessionStorage.getItem('storeName'); // Assuming storeName is stored in session storage

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const data = await apiGetOrdersByStore(storeName, token);
        setOrders(data);
      } catch (error) {
        setError('An error occurred. Please try again.');
      }
    };

    fetchOrders();
  }, [storeName, token]);

  const handleStatusChange = async (orderId, status) => {
    try {
      await apiUpdateOrderStatus(orderId, status, token);
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order.orderId === orderId ? { ...order, status } : order
        )
      );
    } catch (error) {
      setError('Failed to update order status.');
    }
  };

  return (
    <div className="container mt-5">
      <h2>Orders</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {orders.map((order) => (
          <div className="col-md-4 mb-4" key={order.orderId}>
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Order ID: {order.orderId}</h5>
                <p className="card-text"><strong>Status:</strong> {order.status}</p>
                <p className="card-text"><strong>Date:</strong> {new Date(order.orderDate).toLocaleDateString()}</p>
                <p className="card-text"><strong>Items:</strong></p>
                <ul>
                  {order.orderItems.map((item) => (
                    <li key={item.orderItemId}>
                      {item.productName} - Quantity: {item.quantity}
                    </li>
                  ))}
                </ul>
                <div>
                  <button
                    className="btn btn-success mr-2"
                    onClick={() => handleStatusChange(order.orderId, 'Completed')}
                  >
                    Mark as Completed
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleStatusChange(order.orderId, 'Not Completed')}
                  >
                    Mark as Not Completed
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default OrderListPageSeller;
