import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { apiCreateOrder } from '../api';

function OrderPageBuyer() {
  const { storeId } = useParams();
  const [orderItems, setOrderItems] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const token = localStorage.getItem('authToken');

  const handleAddOrderItem = (productId, quantity) => {
    setOrderItems([...orderItems, { productId, quantity }]);
  };

  const handleCreateOrder = async () => {
    try {
      await apiCreateOrder({ storeId, orderItems }, token);
      setSuccess(true);
      setError(null);
    } catch (error) {
      setSuccess(false);
      setError('Failed to create order');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Order from Store {storeId}</h1>
      {/* Add form inputs to select products and quantities */}
      <button onClick={handleCreateOrder} className="btn btn-primary">Place Order</button>
      {success && <div className="alert alert-success mt-4">Order created successfully!</div>}
      {error && <div className="alert alert-danger mt-4">{error}</div>}
    </div>
  );
}

export default OrderPageBuyer;
