// src/components/ProfilePage.js
import React, { useState, useEffect } from 'react';

function ProfilePage() {
  const [profile, setProfile] = useState(null);
  const [error, setError] = useState(null);
  const userId = JSON.parse(sessionStorage.getItem("userId"));
  const token = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:5197/api/User/${userId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });
        if (response.ok) {
          const data = await response.json();
          setProfile(data);
        } else {
          setError('Failed to fetch profile');
        }
      } catch (err) {
        setError('An error occurred. Please try again.');
      }
    };

    fetchProfile();
  }, [userId, token]);

  if (error) {
    return <div className="alert alert-danger">{error}</div>;
  }

  if (!profile) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Profile</h1>
      <div className="card">
        <div className="card-body">
          <h5 className="card-title">Username: {profile.username}</h5>
          <p className="card-text">Email: {profile.email}</p>
          <p className="card-text">Full Name: {profile.fullName}</p>
          <p className="card-text">Role: {profile.role}</p>
        </div>
      </div>
    </div>
  );
}

export default ProfilePage;
