import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function StorePage() {
  const [storeName, setStoreName] = useState("");
  const [logo, setLogo] = useState(null);
  const [stores, setStores] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const userId = JSON.parse(sessionStorage.getItem("userId"));
  const token = localStorage.getItem('authToken');

  const fetchStores = async () => {
    try {
      const response = await fetch(`http://localhost:5197/api/Store/by-user-id/${userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        console.log(data);  // Log the data to check its structure
        setStores(Array.isArray(data) ? data : []);
        setError(null);
      } else {
        setError("Failed to fetch stores");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  useEffect(() => {
    fetchStores();
  }, []);

  const handleCreateStore = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("storeName", storeName);
    formData.append("logo", logo);

    try {
      const response = await fetch('http://localhost:5197/api/store', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });
      if (response.ok) {
        setStoreName("");
        setLogo(null);
        setError(null);
        fetchStores(); // Refresh the store list
      } else {
        setError("Failed to create store");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div>
      <form onSubmit={handleCreateStore}>
        <input
          type="text"
          value={storeName}
          onChange={(e) => setStoreName(e.target.value)}
          placeholder="Store Name"
        />
        <input
          type="file"
          onChange={(e) => setLogo(e.target.files[0])}
        />
        <button type="submit">CREATE STORE</button>
      </form>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <div>
        <h2>Your Stores</h2>
        <ul>
          {stores.map(store => (
            <li key={store.storeId}>
              <div>
                <h3>Store Name: {store.storeName}</h3>
                {store.logoUrl && (
                  <div>
                    <h3>Store Logo:</h3>
                    <img src={`http://localhost:5197${store.logoUrl}`} alt={`${store.storeName} logo`} width="100" />
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default StorePage;
