import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const StorePage = () => {
  const [store, setStore] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const userId = JSON.parse(sessionStorage.getItem("userId"));
  const token = localStorage.getItem('authToken');

  const fetchStore = async () => {
    try {
      const response = await fetch(`http://localhost:5197/api/Store/by-user-id/${userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        setStore(data);
        setError(null);
      } else {
        setError("Failed to fetch store");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  useEffect(() => {
    fetchStore();
  }, []);

  return (
    <div className="container mt-4">
      <h2>Your Store</h2>
      {store ? (
        <div>
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Store Name: {store.storeName}</h5>
              <p className="card-text">
                <img src={`http://localhost:5197${store.logoUrl}`} alt="Store Logo" style={{ width: '100px', height: '100px' }} />
              </p>
            </div>
          </div>
          <button className="btn btn-primary mt-3" onClick={() => navigate('/add-products')}>Add Products</button>
          <button className="btn btn-secondary mt-3 ml-2" onClick={() => navigate('/see-orders')}>See Orders</button>
        </div>
      ) : (
        <div>
          <p>You still don't have a store.</p>
          <button className="btn btn-primary" onClick={() => navigate('/create-store')}>Click here to create your Store</button>
        </div>
      )}
      {error && <div className="alert alert-danger mt-3">{error}</div>}
    </div>
  );
};

export default StorePage;
