import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import StorePage from './components/StorePage';
import CreateStorePage from './components/CreateStorePage';
import ProfilePage from './components/ProfilePage';
import AddProductPage from './components/AddProductPage';
import Navbar from './components/Navbar';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [token, setToken] = useState(localStorage.getItem('authToken'));
  const [role, setRole] = useState(sessionStorage.getItem('userRole'));

  useEffect(() => {
    const storedToken = localStorage.getItem('authToken');
    const storedRole = sessionStorage.getItem('userRole');
    if (storedToken) {
      setToken(storedToken);
    }
    if (storedRole) {
      setRole(storedRole);
    }
  }, []);

  const handleLoginSuccess = (token, role) => {
    setToken(token);
    setRole(role);
    localStorage.setItem('authToken', token);
    sessionStorage.setItem('userRole', role);
  };

  return (
    <Router>
      {token && <Navbar role={role} />}
      <Routes>
        <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/store" element={token && role === 'seller' ? <StorePage /> : <Navigate to="/login" />} />
        <Route path="/create-store" element={token && role === 'seller' ? <CreateStorePage /> : <Navigate to="/login" />} />
        <Route path="/profile" element={token ? <ProfilePage /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
