import React, { useEffect, useState } from 'react';
import { apiGetOrdersByStoreId, apiUpdateOrderStatus } from '../api';

function OrderListPageSeller() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');
  const storeId = sessionStorage.getItem('storeId');

  const fetchOrders = async () => {
    try {
      const data = await apiGetOrdersByStoreId(storeId, token);
      setOrders(data);
    } catch (err) {
      setError('Failed to fetch orders');
    }
  };

  const handleStatusChange = async (orderId, newStatus) => {
    try {
      await apiUpdateOrderStatus(orderId, newStatus, token);
      fetchOrders(); // Refresh orders after status update
    } catch (err) {
      setError('Failed to update order status');
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="container mt-5">
      <h1>Orders</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <ul className="list-group">
        {orders.map(order => (
          <li key={order.orderId} className="list-group-item">
            <h5>Order ID: {order.orderId}</h5>
            <p>Status: {order.status}</p>
            <p>Order Date: {new Date(order.orderDate).toLocaleString()}</p>
            <p>User: {order.userId}</p>
            <div>
              <label htmlFor="status">Change Status: </label>
              <select
                id="status"
                value={order.status}
                onChange={e => handleStatusChange(order.orderId, e.target.value)}
              >
                <option value="Pending">Pending</option>
                <option value="Completed">Completed</option>
                <option value="Not Completed">Not Completed</option>
              </select>
            </div>
            <ul>
              {order.orderItems.map(item => (
                <li key={item.orderItemId}>
                  <p>Product: {item.productId}</p>
                  <p>Quantity: {item.quantity}</p>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default OrderListPageSeller;
