import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { apiGetOrdersByStore } from '../api';

function OrderListPage() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');
  const userId = sessionStorage.getItem('userId');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch(`http://localhost:5197/api/Order/by-user-id/${userId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setOrders(data);
        } else {
          setError('Failed to fetch orders');
        }
      } catch (error) {
        setError('An error occurred. Please try again.');
      }
    };

    fetchOrders();
  }, [token, userId]);

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Orders</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {orders.map((order) => (
          <div className="col-md-4 mb-4" key={order.orderId}>
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Order ID: {order.orderId}</h5>
                <p className="card-text"><strong>Status:</strong> {order.status}</p>
                <p className="card-text"><strong>Order Date:</strong> {new Date(order.orderDate).toLocaleDateString()}</p>
                <p className="card-text"><strong>User ID:</strong> {order.userId}</p>
                <button className="btn btn-warning" onClick={() => navigate(`/edit-order/${order.orderId}`)}>Edit Status</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default OrderListPage;
