import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

function StorePage() {
  const [storeName, setStoreName] = useState("");
  const [logo, setLogo] = useState(null);
  const [store, setStore] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const userId = JSON.parse(sessionStorage.getItem("userId"));
  const token = localStorage.getItem('authToken');

  const fetchStore = async () => {
    try {
      const response = await fetch(`http://localhost:5197/api/Store/by-user-id/${userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        console.log("Fetched store:", data);
        setStore(data);
        setError(null);
      } else {
        setStore(null);
        setError("Failed to fetch store");
      }
    } catch (error) {
      setStore(null);
      setError("An error occurred. Please try again.");
    }
  };

  useEffect(() => {
    fetchStore();
  }, []);

  const handleCreateStore = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("storeName", storeName);
    formData.append("logo", logo);

    try {
      const response = await fetch('http://localhost:5197/api/store', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });
      if (response.ok) {
        setStoreName("");
        setLogo(null);
        setMessage("");
        fetchStore(); // Refresh the store
      } else {
        setError("Failed to create store");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  const handleCreateStoreClick = () => {
    if (store) {
      setMessage("You already have a store.");
    } else {
      navigate('/create-store');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Store</h1>
      {store ? (
        <div className="card mb-4">
          <div className="card-body">
            <h5 className="card-title">Store Name: {store.storeName}</h5>
            {store.logoUrl && (
              <div>
                <h6>Store Logo:</h6>
                <img src={`http://localhost:5197${store.logoUrl}`} alt="Store Logo" className="img-fluid" />
              </div>
            )}
          </div>
        </div>
      ) : (
        <div>
          <p className="text-muted">You still don't have a store.</p>
        </div>
      )}
      <button onClick={handleCreateStoreClick} className="btn btn-primary mb-4">
        {store ? "You already have a store" : "Click here to create your Store"}
      </button>
      {message && <div className="alert alert-warning">{message}</div>}
      {error && <div className="alert alert-danger">{error}</div>}
    </div>
  );
}

export default StorePage;
