import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function StorePage() {
  const [storeName, setStoreName] = useState("");
  const [logo, setLogo] = useState("");
  const [store, setStore] = useState(null); // Change to store object
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const userId = JSON.parse(sessionStorage.getItem("userId"));
  const token = localStorage.getItem('authToken');

  const fetchStore = async () => {
    try {
      const response = await fetch(`http://localhost:5197/api/Store/by-user-id/${userId}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      if (response.ok) {
        const data = await response.json();
        console.log("Fetched store:", data);  // Log the data to check its structure
        setStore(data);
        setError(null);
      } else {
        setError("Failed to fetch store");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  useEffect(() => {
    fetchStore();
  }, []);

  const handleCreateStore = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("storeName", storeName);
    formData.append("logo", logo);

    try {
      const response = await fetch('http://localhost:5197/api/store', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });
      if (response.ok) {
        setStoreName("");
        setLogo(null);
        setError(null);
        fetchStore(); // Refresh the store
      } else {
        setError("Failed to create store");
      }
    } catch (error) {
      setError("An error occurred. Please try again.");
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Your Store</h1>
      <form onSubmit={handleCreateStore} className="mb-5">
        <div className="mb-3">
          <label htmlFor="storeName" className="form-label">Store Name</label>
          <input
            type="text"
            className="form-control"
            id="storeName"
            value={storeName}
            onChange={(e) => setStoreName(e.target.value)}
            placeholder="Store Name"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="storeLogo" className="form-label">Store Logo</label>
          <input
            type="file"
            className="form-control"
            id="storeLogo"
            onChange={(e) => setLogo(e.target.files[0])}
          />
        </div>
        <button type="submit" className="btn btn-primary">Create Store</button>
      </form>
      {error && <div className="alert alert-danger">{error}</div>}
      {store ? (
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Store Name: {store.storeName}</h5>
            {store.logoUrl && (
              <div>
                <h6>Store Logo:</h6>
                <img src={`http://localhost:5197${store.logoUrl}`} alt="Store Logo" className="img-fluid" />
              </div>
            )}
          </div>
        </div>
      ) : (
        <p>No store found.</p>
      )}
    </div>
  );
}

export default StorePage;
