import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ProductListBuyer from './ProductListBuyer';
import 'bootstrap/dist/css/bootstrap.min.css';

function StorePageBuyer() {
  const { storeId } = useParams();
  const [store, setStore] = useState(null);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchStore = async () => {
      try {
        const response = await fetch(`http://localhost:5197/api/Store/${storeId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setStore(data);
          setError(null);
        } else {
          setError('Failed to fetch store');
        }
      } catch (error) {
        setError('An error occurred. Please try again.');
      }
    };

    fetchStore();
  }, [storeId, token]);

  return (
    <div className="container mt-5">
      {store ? (
        <div>
          <h1 className="mb-4">Store: {store.storeName}</h1>
          {store.logoUrl && (
            <div>
              <img src={`http://localhost:5197${store.logoUrl}`} alt="Store Logo" className="img-fluid" />
            </div>
          )}
          <ProductListBuyer storeId={store.storeId} />
        </div>
      ) : (
        <div>
          <h1 className="mb-4">Store</h1>
          {error && <div className="alert alert-danger">{error}</div>}
        </div>
      )}
    </div>
  );
}

export default StorePageBuyer;
