import React, { useEffect, useState } from 'react';
import { apiGetOrdersByStoreId, apiUpdateOrderStatus } from '../api';
import { useParams } from 'react-router-dom';

function OrderPageSeller() {
  const { storeId } = useParams();
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const orders = await apiGetOrdersByStoreId(storeId, token);
        setOrders(orders);
      } catch (error) {
        setError('Failed to fetch orders');
      }
    };

    fetchOrders();
  }, [storeId, token]);

  const handleUpdateStatus = async (orderId, status) => {
    try {
      await apiUpdateOrderStatus(orderId, status, token);
      setOrders(orders.map(order => (order.orderId === orderId ? { ...order, status } : order)));
      setError(null);
    } catch (error) {
      setError('Failed to update order status');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Orders for Store {storeId}</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      {orders.map(order => (
        <div key={order.orderId} className="card mb-4">
          <div className="card-body">
            <h5 className="card-title">Order ID: {order.orderId}</h5>
            <p className="card-text"><strong>Status:</strong> {order.status}</p>
            {/* Display order items here */}
            <button onClick={() => handleUpdateStatus(order.orderId, 'Completed')} className="btn btn-success">Mark as Completed</button>
            <button onClick={() => handleUpdateStatus(order.orderId, 'Not Completed')} className="btn btn-danger">Mark as Not Completed</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default OrderPageSeller;
