import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { apiCreateOrder } from '../api';

function OrderPage() {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem('authToken');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(`http://localhost:5197/api/Product`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setProducts(data);
        } else {
          setError('Failed to fetch products');
        }
      } catch (error) {
        setError('An error occurred. Please try again.');
      }
    };

    fetchProducts();
  }, [token]);

  const handleAddToOrder = (productId) => {
    setSelectedProducts([...selectedProducts, { productId, quantity: 1 }]);
  };

  const handleOrderSubmit = async () => {
    try {
      const storeId = products[0]?.storeId; // Assume all products are from the same store
      await apiCreateOrder({ storeId, orderItems: selectedProducts }, token);
      navigate('/profile');
    } catch (error) {
      setError('Failed to create order');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Order Products</h1>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {products.map((product) => (
          <div className="col-md-4 mb-4" key={product.productId}>
            <div className="card">
              <img src={`http://localhost:5197${product.imageUrl}`} className="card-img-top" alt={product.productName} />
              <div className="card-body">
                <h5 className="card-title">{product.productName}</h5>
                <p className="card-text">{product.description}</p>
                <p className="card-text"><strong>Price:</strong> ${product.price}</p>
                <p className="card-text"><strong>Stock:</strong> {product.stockQuantity}</p>
                <button className="btn btn-primary" onClick={() => handleAddToOrder(product.productId)}>Add to Order</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <button className="btn btn-success mt-3" onClick={handleOrderSubmit}>Submit Order</button>
    </div>
  );
}

export default OrderPage;
