import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import Profile from './components/Profile';
import StorePage from './components/StorePage';
import CreateStorePage from './components/CreateStorePage';
import './App.css';

function App() {
  const [token, setToken] = useState(localStorage.getItem('authToken'));
  const [role, setRole] = useState(localStorage.getItem('userRole'));

  const handleLoginSuccess = (token, role) => {
    setToken(token);
    setRole(role);
    localStorage.setItem('authToken', token);
    localStorage.setItem('userRole', role);
  };

  return (
    <Router>
      <Navbar role={role} />
      <Routes>
        <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/profile" element={token ? <Profile /> : <Navigate to="/login" />} />
        <Route path="/store" element={token && role === 'seller' ? <StorePage /> : <Navigate to="/login" />} />
        <Route path="/create-store" element={token && role === 'seller' ? <CreateStorePage /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
