// src/pages/StorePage.js
import React, { useState, useEffect } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

function StorePage() {
  const [storeName, setStoreName] = useState('');
  const [stores, setStores] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await fetch('http://localhost:5197/api/store', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
        },
      });
      const data = await response.json();
      if (response.ok) {
        setStores(data);
      } else {
        setError(data.message || 'Failed to fetch stores');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  const handleCreateStore = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('storeName', storeName);
    // Add logo file to formData if needed
    // formData.append('logo', logoFile);
    try {
      const response = await fetch('http://localhost:5197/api/store', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('authToken')}`,
        },
        body: formData,
      });
      const data = await response.json();
      if (response.ok) {
        setStoreName('');
        setError(null);
        fetchStores();
      } else {
        setError(data.message || 'Failed to create store');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <form onSubmit={handleCreateStore}>
        <TextField
          variant="outlined"
          value={storeName}
          onChange={(e) => setStoreName(e.target.value)}
          label="Store Name"
          fullWidth
          margin="normal"
        />
        {error && <div style={{ color: 'red' }}>{error}</div>}
        <Button type="submit" variant="contained" color="primary">
          Create Store
        </Button>
      </form>

      <h2>Your Stores</h2>
      <ul>
        {stores.map(store => (
          <li key={store.storeId}>{store.storeName}</li>
        ))}
      </ul>
    </div>
  );
}

export default StorePage;
